#pragma config(Motor,  port2,           RightWheel,    tmotorServoContinuousRotation, openLoop, driveLeft)
#pragma config(Motor,  port4,           ArmMotor,      tmotorServoContinuousRotation, openLoop)
#pragma config(Motor,  port5,           HandServo,     tmotorServoStandard, openLoop)
#pragma config(Motor,  port7,           WristServo,    tmotorServoStandard, openLoop)
#pragma config(Motor,  port9,           LeftWheel,     tmotorServoContinuousRotation, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

int i = 0;
int j = 0;
#include <arm.c>
#include <handServo.c>
#include <wristServo.c>

task main() {
	startTask(armStart);
	while(true) {
		int leftSpeed = 0;
		int rightSpeed = 0;
		int left2Speed = 0;
		int right2Speed = 0;
		//Null Zone Left Stick
		if(vexRT[Ch3] < 20 && vexRT[Ch3] > -20) {
			//If the Left Joystick is in the Middle, set motors to Zero
			rightSpeed = 0;
			leftSpeed = 0;
			} else {
			//Set Motors speed to be Ch3
			rightSpeed = -vexRT[Ch3];
			leftSpeed = -vexRT[Ch3];
		}

		//Null Zone Left Stick
		if(vexRT[Ch4] < 20 && vexRT[Ch4] > -20) {
			//If the Left Joystick is in the Middle, set motors to Zero
			right2Speed = 0;
			left2Speed = 0;
			} else {
			//Set Motors speed to be Ch3
			right2Speed = vexRT[Ch4];
			left2Speed = vexRT[Ch4];
		}
		if(vexRT[Btn6U] == 1) {
			startTask(handServoStart);
	  } else if(vexRT[Btn6D] == 1){
	  	startTask(handServoReverse);
		}
		if(vexRT[Btn5U] == 1) {
			startTask(wristServoStart);
	  } else if(vexRT[Btn5D] == 1){
	  	startTask(wristServoReverse);
		}
		//Set Motors to the values
		motor[RightWheel]= 0 + rightSpeed + right2Speed;
		motor[LeftWheel]= 0 + (-leftSpeed) - (-left2Speed);

wait1Msec(20);
	}
}
